import React from 'react'
import CustomBtn from './CustomBtn'
import logo from '../Assets/logo.svg'
import logoMobile from '../Assets/logoMobile.svg'
import { Toolbar, Typography } from '@material-ui/core'
import { makeStyles } from "@material-ui/core/styles";
import '../App.css'




const styles = makeStyles({
    bar: {
        paddingTop: "1.15rem",
        backgroundColor: "#fff",
        // eslint-disable-next-line
        ['@media (max-width:780px)']: {
            flexDirection: "column"
        }
    },
    logo: {
        width: "15%",
        height: "75px",
        // eslint-disable-next-line
        ['@media (max-width:780px)']: {
            display: "none"
        }
    },
    logoMobile: {
        width: "40%",
        display: "none",
        // eslint-disable-next-line
        ['@media (max-width:780px)']: {
            display: "inline-block"
        }
    },
    menuItem: {
        cursor: "pointer",
        flexGrow: 1,
        "&:hover": {
            color: "#4f25c8"
        },
        // eslint-disable-next-line
        ['@media (max-width:780px)']: {
            paddingBottom: "1rem"
        }
    }
})

function NavBar() {


    const classes = styles()
    return (
        <Toolbar position="sticky" color="rgba(0, 0, 0, 0.87)" className={classes.bar}>
            <img src={logo} className={classes.logo} alt="" />
            <img src={logoMobile} className={classes.logoMobile} alt="" />
            <Typography variant="h6" className={classes.menuItem}>
                Home
            </Typography>
            <Typography variant="h6" className={classes.menuItem}>
                Sobre
            </Typography>
            <Typography variant="h6" className={classes.menuItem}>
                Advogados
            </Typography>
            <Typography variant="h6" className={classes.menuItem}>
                Casos
            </Typography>
            <Typography variant="h6" className={classes.menuItem}>
                Contactos
            </Typography>
            <button type="button" className="myButton" data-bs-toggle="modal" data-bs-target="#exampleModal">
                <CustomBtn txt="Marque ReuniÃ£o"/>
            </button>


            <div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div class="modal-dialog">
                    <div class="modal-content">
                        <div class="modal-header">
                            <h5 class="modal-title" id="exampleModalLabel">Modal title</h5>
                            <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div class="modal-body">
                            ...
                        </div>
                        <div class="modal-footer">
                            <button type="button" class="myButton" data-bs-dismiss="modal"><CustomBtn txt="Log In"/></button>
                            <button type="button" class="btn btn-primary">Save changes</button>
                        </div>
                    </div>
                </div>
            </div>
            <CustomBtn txt="Log In/ Sign In" />
        </Toolbar>
    )
}

export default NavBar
